class PersistentVerifyView(View):
    def __init__(self, role_id=None, button_label="Verify", button_style=discord.ButtonStyle.green):
        super().__init__(timeout=None)
        if role_id:
            button = Button(label=button_label, style=button_style, custom_id=f"verify_{role_id}")
            button.callback = self.verify_callback
            self.add_item(button)

    async def verify_callback(self, interaction: discord.Interaction):
        guild = interaction.guild
        member = interaction.user
        custom_id = interaction.data.get("custom_id", "")

        if "_" in custom_id:
            try:
                role_id = int(custom_id.split("_")[1])
                role = guild.get_role(role_id)
                
                if role:
                    if role not in member.roles:
                        await member.add_roles(role)
                        await interaction.response.send_message("‚úÖ You have been verified!", ephemeral=True)
                    else:
                        await interaction.response.send_message("‚ö†Ô∏è You are already verified!", ephemeral=True)
                else:
                    await interaction.response.send_message("‚ùå Role not found! Please contact an admin.", ephemeral=True)
            except ValueError:
                await interaction.response.send_message("‚úÖ Verification successful!", ephemeral=True)
        else:
            await interaction.response.send_message("‚úÖ Verification successful!", ephemeral=True)


